mapper.select:
SELECT COUNT(*)
FROM   MAIN_TASK
WHERE  USER_ID = #{followeeId}

SELECT COUNT(*)
FROM   REPLY
WHERE  USER_ID = #{followeeId}

SELECT COUNT(*)
FROM   FOLLOW
WHERE  FOLLOWEE_ID = #{followeeId}

SELECT COUNT(*)
FROM   MAIN_TASK
WHERE  USER_ID = #{followerId}

SELECT COUNT(*)
FROM   REPLY
WHERE  USER_ID = #{followerId}

SELECT COUNT(*)
FROM   FOLLOW
WHERE  FOLLOWER_ID = #{followerId}

mapper.insert:
INSERT	INTO FOLLOW(
		FOLLOWEE_ID,
		FOLLOWER_ID
)
VALUES (
	#{followeeId},
	#{followerId}
)

mapper.delete:
DELETE	FROM FOLLOW
WHERE	FOLLOWER_ID = #{followerId},
AND		FOLLOWEE_ID = #{followeeId}
