mapper.select:
SELECT EMAIL
FROM USER
WHERE EMAIL = #{email}

SELECT NICKNAME
FROM USER
WHERE NICKNAME = #{nickname}

mapper.update:
UPDATE USER SET NICKNAME =#{nickname}, PHONE_NUMBER = #{phoneNumber}, ZIPCODE = #{zipcode}, ROAD_ADDRESS = #{roadAddress}, JIBUN_ADDRESS = #{jibunAddress}, ETC_ADDRESS = #{etcAddress}, PROFILE_ORIGINAL_NAME = #{profileOriginalName}, PROFILE_UUID = #{profileUuid}, AMBITION_QUOTE = #{ambitionQuote}, MODIFIED_AT=NOW()
, PASSWORD = #{password}
WHERE USER_ID=#{userId}

mapper.select:
SELECT COUNT(*)
FROM FOLLOW
WHERE FOLLOWER_ID = #{userId}

SELECT COUNT(*)
FROM FOLLOW
WHERE FOLLOWEE_ID = #{userId}
